mapper.select:
SELECT file.id,file.filename,file.filesize,user_file.vfname,user_file.virtualpath,user_file.uptime,file.filetype
FROM users,file,user_file
WHERE users.id=user_file.uid AND file.id=user_file.fid AND users.id = #{user.id}

SELECT file.id as id,file.filename as filename,file.filesize as filesize,
       user_file.vfname as vfname,user_file.virtualpath as virtualpath,
       user_file.uptime as uptime,file.filetype as filetype
FROM users,file,user_file
WHERE users.id=user_file.uid AND file.id=user_file.fid AND users.id = #{id}
LIMIT ${page},${limit}
